import React, { useEffect, useState } from "react";
import axios from "axios";
import {
    Row,
    Table,
    Button,
    Container,
    Modal,
    Form

} from "react-bootstrap";
import moment from "moment";
import MainHeader from "../header/Header_pages";
import { useParams, useHistory } from "react-router";
import FloatingLabel from "react-bootstrap-floating-label";
import { API_URL } from "../../utils/constant";
import "../style.css";
// import { func } from 'prop-types';

const IssueNote = () => {

    const [allitems, setallitems] = useState([]);
    useEffect(() => {

        axios.get(API_URL + '/storeitem').then(res => {
            console.log(res.data);
            setallitems(res.data);
        });


    }, []);




    const [date, setdate] = useState(moment(Date.now()).format('YYYY-MM-DD'));
    const [department, setdepartment] = useState("");
    const [items, setitems] = useState([{ item: "", quantity: "", index: undefined, reason: "" }]);


    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const list = [...items];

        list[index][name] = value;
        list[index].index = allitems.map(e => e.Item_ID.Item_Name).indexOf(e.target.value);

        setitems(list);

    };

    const handleInputChangereason = (e, index) => {
        const { name, value } = e.target;
        const list = [...items];

        list[index][name] = value;

        setitems(list);

    };
    const handleInputChangequantity = (e, index) => {
        let { value, min, max } = e.target;
        value = Math.max(Number(min), Math.min(Number(max), Number(value)));
        const list = [...items];
        list[index].quantity = value;
        setitems(list);

    };


    // handle click event of the Remove button
    const handleRemoveClick = index => {
        const list = [...items];
        list.splice(index, 1);
        setitems(list);
    };

    // handle click event of the Add button
    const handleAddClick = () => {
        setitems([...items, { item: "", quantity: "", index: undefined, reason: "" }]);
    };

    const Submit = e => {
        e.preventDefault();

    }


    return (
        <>
            <MainHeader />
            <Container className="Container1 justify-content-center">
                <Form className="w-100 FormStyle" onSubmit={Submit} >
                    <h1 className="Heading">Issue Note</h1>
                    <Form.Group className="FormGroup">
                        <Form.Label className='CenterLabel'>Date</Form.Label>
                        <Form.Control
                            type="date"
                            value={date}
                            defaultValue={Date.now().toLocaleString()}
                            onChange={(e) => { setdate(e.target.value) }}
                            required
                        />
                    </Form.Group>
                    <Form.Group className="FormGroup">
                        <Form.Label className="CenterLabel">Department</Form.Label>
                        <Form.Control
                            type="text"
                            value={department}
                            placeholder="Department"
                            onChange={(e) => { setdepartment(e.target.value) }}
                            required
                        />
                    </Form.Group>

                    <h4 className="w-100 CenterLabel">Items<Button onClick={handleAddClick} className="float-right btn-success m-2">+</Button></h4>
                    {items.map((x, i) => {
                        return (
                            <Form.Group className="FormGroup w-100">
                                <Form.Floating controlId="floatingSelectGrid" label="Item">
                                    <Form.Select name="item" className="w-100" aria-label="Floating label select example" onChange={(e) => { handleInputChange(e, i) }} required>
                                        <option disabled selected>-</option>
                                        {
                                            allitems.map((item, index) => {
                                                return (
                                                    <option>{item.Item_ID.Item_Name}</option>
                                                )
                                            })
                                        }
                                    </Form.Select>
                                </Form.Floating>
                                <FloatingLabel controlId="floatingSelectGrid" label="Works with selects">
                                    <Form.Select aria-label="Floating label select example">
                                        <option>Open this select menu</option>
                                        <option value="1">One</option>
                                        <option value="2">Two</option>
                                        <option value="3">Three</option>
                                    </Form.Select>
                                </FloatingLabel>
                                <FloatingLabel label="Quantity">
                                    <Form.Control
                                        className="ml-3"
                                        type="Number"
                                        Min="1"
                                        Max={items[i].index !== undefined ? allitems[items[i].index].Quantity : 1}
                                        name="quantity"
                                        placeholder={items[i].index !== undefined ? "Min : 1 - Max : " + allitems[items[i].index].Quantity : "Quantity"}
                                        value={x.quantity}
                                        onChange={e => handleInputChangequantity(e, i)}
                                        required
                                    />
                                </FloatingLabel>
                                <FloatingLabel label="Reason">
                                    <Form.Control
                                        className="ml-3"
                                        type="text"
                                        name="reason"
                                        placeholder="Reason"
                                        value={x.reason}
                                        onChange={e => handleInputChangereason(e, i)}
                                    />

                                    {items.length !== 1 && <Button
                                        className="ml-3 btn-danger"
                                        onClick={() => handleRemoveClick(i)}>X</Button>}
                                    {/* {items.length - 1 === i && <Button onClick={handleAddClick} className="ml-3 btn-success">+</Button>} */}
                                </FloatingLabel>

                            </Form.Group>

                        )
                    })}



                    <Button className="btn-lg float-right Btn" type="submit">Issue</Button>
                </Form>
            </Container>
        </>
    );

};

export default IssueNote;
