import axios from 'axios';
import {Button, Table, Form, Col, Container} from 'react-bootstrap'
import '../style.css'
import HeaderPages from '../header/Header_pages';
import { useState } from 'react';
import { API_URL } from '../../utils/constant';
import moment from 'moment';


const ProductLedger = () =>{

const [start_Date , set_Start_Date] = useState();
const [Product , set_Product] = useState();
const [end_Date , set_End_Date] = useState();
const arr = ['Date', 'Quantity', 'Duration', 'Status']

const [allRecords, setRecords] = useState([]);

const Submit = async(e)=>{
    e.preventDefault();
    fetch(`${API_URL}/generateproductledger`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            start_date : start_Date, product : Product, end_date : end_Date
        }),
      }).then(async res => {
        if (res.status === 200) {
          const blob = await res.blob();
          const file = new Blob(
            [blob], 
            {type: 'application/pdf'},
            {name : 'report.pdf'}
          );
          console.log(file)
          
          //Build a URL from the file
          const fileURL = URL.createObjectURL(file);
          //Open the URL on new Window
          console.log(fileURL)
        //   download(res.data, details.request.Item + '_report', 'pdf');
          window.open(fileURL);  
        }
      })
}

// const Submit = (event) => {
//     event.preventDefault();
//     axios.post(`${API_URL}/generateproductledger`, { start_date : start_Date, product : Product, end_date : end_Date }).then((res)=>{
//         console.log(res.data);
// })
// }




return(
    <>
    <HeaderPages />
    <Container className='Container_Date'>
        <h1>Product Ledger</h1>
        <Form  onSubmit = {Submit}>
            <Form.Row>
                    <Form.Group as = {Col} className = "mt-4"  >
                    <Form.Label>Start Date</Form.Label>
                    <Form.Control type = "Date" value = {start_Date} onChange = {(e)=> {set_Start_Date(e.target.value)}} required />
                    </Form.Group>
                    
                    <Form.Group as = {Col} className = "mt-4"  >
                    <Form.Label>Product</Form.Label>
                    <Form.Control type = "text" value = {Product} onChange = {(e)=> {set_Product(e.target.value)}} placeholder = "Product Name" required/>
                    </Form.Group>
                    
                    <Form.Group as = {Col} className = "mt-4" >
                    <Form.Label>End Date</Form.Label>
                    <Form.Control type = "Date" value = {end_Date} onChange = {(e)=> {set_End_Date(e.target.value)}} required/>
                    </Form.Group>
                
                <Form.Group as = {Col} className = "mt-4">
                <Button className = "Btn Date_Button ml-2" type = 'Submit'>Generate</Button>
                </Form.Group>

            </Form.Row>
        </Form>
    </Container>


    {allRecords.length > 0 &&
        <Table className="TableStyle" responsive>
            <thead>
                <>
                    <tr>
                        <th>#</th>
                        {arr.map((_, index) => (
                            <th key={index}>{_}</th>
                        ))}
                        <th></th>
                    </tr>
                </>
            </thead>
            <tbody>
                {allRequests.map((request, index) => {
                    if (request.Item.toLowerCase().includes(itemName.toLowerCase()))
                        return (
                            <>
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>{request.R_Emp_Name}<br /></td>
                                    <td>{request.Item}</td>
                                    <td>{request.Quantity}</td>
                                    <td>{moment(request.Added).format('Do MMMM YYYY')}</td>
                                    <td><Button className="Btn" onClick={() => { clearInterval(interval_id.current); SetInfo(request); setShow(true); }} >Recieved</Button></td>


                                </tr>


                            </>
                        )
                    else return (<tr></tr>)


                })}
            </tbody>
        </Table>

    }
    {
        allRecords.length === 0 && <h2>No Records.</h2>
    }


    </>

)


}

export default ProductLedger;
