import React, { useEffect, useState } from "react";
import axios from "axios";
import {
    Row,
    Table,
    Button,
    Container,
    Modal,
    Form,
    Card,
} from "react-bootstrap";
import moment from "moment";
import MainHeader from "../header/Header_pages";
import { useParams, useHistory } from "react-router";
import { API_URL } from "../../utils/constant";
import "../style.css";
// import { func } from 'prop-types';

const IssueNote = () => {
    const [date, setdate] = useState(moment(Date.now()).format('YYYY-MM-DD'));
    const [department, setdepartment] = useState(moment(Date.now()).format('YYYY-MM-DD'));
    const [requestid, setrequestid] = useState(moment(Date.now()).format('YYYY-MM-DD'));

    return (
        <>
            <MainHeader />
            <Container className="Container1">
                <Form className="FormStyle">
                    <Form.Label>Date</Form.Label>
                    <Form.Control
                        type="date"
                        value={date}
                        defaultValue={Date.now().toLocaleString()}
                        onChange={(e) => { setdate(e.target.value) }}
                    />

                    <Form.Label className>Department</Form.Label>
                    <Form.Control
                        type="text"
                        value={department}
                        onChange={(e) => { setdepartment(e.target.value) }}
                    />
                </Form>
            </Container>
        </>
    );

};

export default IssueNote;
