import React, { useEffect, useState } from "react";
import axios from "axios";
import {
    Row,
    Table,
    Button,
    Container,
    Modal,
    Form,
    Card,
} from "react-bootstrap";
import moment from "moment";
import MainHeader from "../header/Header_pages";
import { useParams, useHistory } from "react-router";
import { API_URL } from "../../utils/constant";
import "../style.css";
// import { func } from 'prop-types';

const IssueNote = () => {

    const [allitems, setallitems] = useState([]);
    useEffect(() => {

        axios.get(API_URL + '/storeitem').then(res => {
            console.log(res.data);
            setallitems(res.data);
        });


    }, []);




    const [date, setdate] = useState(moment(Date.now()).format('YYYY-MM-DD'));
    const [department, setdepartment] = useState("");
    const [items, setitems] = useState([{ item: "", quantity: "" }]);
    // const [indexes, setindex] = useState(null)

    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const list = [...items];
        list[index][name] = value;
        setitems(list);

    };

    // handle click event of the Remove button
    const handleRemoveClick = index => {
        const list = [...items];
        list.splice(index, 1);
        setitems(list);
    };

    // handle click event of the Add button
    const handleAddClick = () => {
        setitems([...items, { item: "", quantity: "" }]);
    };


    return (
        <>
            <MainHeader />
            <Container className="Container1 justify-content-center">
                <Form className="FormStyle">
                    <h1 className="Heading">Issue Note</h1>
                    <Form.Group className="FormGroup">
                        <Form.Label className='CenterLabel'>Date</Form.Label>
                        <Form.Control
                            type="date"
                            value={date}
                            defaultValue={Date.now().toLocaleString()}
                            onChange={(e) => { setdate(e.target.value) }}
                        />
                    </Form.Group>
                    <Form.Group className="FormGroup">
                        <Form.Label className="CenterLabel">Department</Form.Label>
                        <Form.Control
                            type="text"
                            value={department}
                            placeholder="Department"
                            onChange={(e) => { setdepartment(e.target.value) }}
                        />
                    </Form.Group>

                    <h4 className="CenterLabel">Items</h4>
                    {items.map((x, i) => {
                        return (
                            <Form.Group className="FormGroup w-100">
                                <Form.Control name="item" as="select" className="selectstyle" onChange={(e) => { handleInputChange(e, i) }} required>
                                    <option disabled selected>-</option>
                                    {
                                        allitems.map((item, index) => {
                                            return (
                                                <option>{item.Item_ID.Item_Name}</option>
                                            )
                                        })
                                    }
                                </Form.Control>
                                <Form.Label className="CenterLabel w-100">Remaining Quantity :  {allitems[allitems.map(e => e.Item_ID.Item_Name).indexOf(items[i].item)] ? allitems[allitems.map(e => e.Item_ID.Item_Name).indexOf(items[i].item)].Quantity : ""}</Form.Label>
                                <Form.Control
                                    className="ml-3"
                                    name="quantity"
                                    placeholder="Quantity"
                                    value={x.quantity}
                                    onChange={e => handleInputChange(e, i)}
                                />

                                {items.length !== 1 && <Button
                                    className="ml-3"
                                    onClick={() => handleRemoveClick(i)}>Remove</Button>}
                                {items.length - 1 === i && <Button onClick={handleAddClick} className="ml-3">Add</Button>}

                            </Form.Group>

                        )
                    })}




                </Form>
            </Container>
        </>
    );

};

export default IssueNote;
